class triangle:
    def triangle():
        h=int(input("Height="))
        b=int(input("Breadth="))
        areaformula=(h*b)/2
        print("Area Formula: (Height*Breadth)/2:")
        print("Area of Triangle:",areaformula)
        h1=int(input("Height1:"))
        h2=int(input("Height2:"))
        h3=int(input("Breadth:"))
        print("Perimeter formula:","h1+h2+h3")
        add=h1+h2+h3
        print("Perimeter of Triangle:",add)
    